import React, { useState } from 'react';
import { LineChart, Line, XAxis, YAxis, Tooltip, CartesianGrid, ResponsiveContainer } from 'recharts';
import './weekly-temp.css';

// Chart Component
const Temp = ({ data }) => {
  const dataParse = data.list;

  // Modify map function to take data from every third entry
  const chartData = dataParse.filter((item, index) => index % 3 === 0).map((item) => ({
    time: Number(item.dt_txt.slice(11, 13)),
    temperature: item.main.temp,
  }));

  const [chartOptions, setChartOptions] = useState({
    title: "Weekly Temp",
    data: chartData,
    axes: [{ type: 'ordinal', position: 'bottom', tick: { count: 25 } }, { type: 'number', position: 'right' }],
  });

  return (
    <ResponsiveContainer className='vis'>
      <LineChart data={chartOptions.data}>
        <XAxis dataKey="time" type="number" />
        <YAxis />
        <CartesianGrid strokeDasharray="3 3" />
        <Tooltip />
        <Line type="line" dataKey="temperature" />
      </LineChart>
    </ResponsiveContainer>
  );
};

export default Temp;
