import './App.css';
import Search from './components/search/search';
import Forecast from './components/forcast/forecast';
import CurrentWeather from './components/current-weather/current-weather';
import Temp from './components/charts/weekly-temp';
import { WEATHER_API_KEY, WEATHER_API_URL } from './api';
import { useDispatch, useSelector } from 'react-redux';
import { fetchWeatherData } from './store';



function App() {
  const dispatch = useDispatch();
  const currentWeather = useSelector((state) => state.currentWeather.data);
  const forecastWeather = useSelector((state) => state.forecastWeather.data);
  const chartData = useSelector((state) => state.chartData.data);

  console.log('currentWeather', currentWeather)
  console.log('forecastWeather', forecastWeather)


  const handleOnSearchChange = (searchData) => {
    const [lat, lon] = searchData.value.split(" ");
    const city = searchData.label
    dispatch(fetchWeatherData(searchData))
    // const currentWeatherFetch = fetch(`${WEATHER_API_URL}/weather?lat=${lat}&lon=${lon}&appid=${WEATHER_API_KEY}&units=metric`)
    // const forecastFetch = fetch(`${WEATHER_API_URL}/forecast?lat=${lat}&lon=${lon}&appid=${WEATHER_API_KEY}&units=metric`)

    Promise.all([currentWeatherFetch, forecastFetch])
      .then(async (response) => {
        const weather = await response[0].json();
        const forecast = await response[1].json();

        dispatch({ type: 'SET_CURRENT_WEATHER', payload: { city: city, ...weather } });
        dispatch({ type: 'SET_FORECAST_WEATHER', payload: forecast });
        dispatch({ type: 'SET_CHART_DATA', payload: forecast });
      })
      .catch((err) => console.log(err));
  }

  console.log('currentWeather', currentWeather)
  console.log('forecastWeather', forecastWeather)

  return (
    <div className="body">
      <div className="search">
        <Search onSearchChange={handleOnSearchChange} />
      </div>
      <div className='main-weather' /* hidden={null == currentWeather } */>
        {currentWeather && <CurrentWeather data={dispatch(currentWeather)} />}
      </div>
      <div className='temp-chart' /* hidden={null == chartData} */>
        {chartData  && <Temp data={dispatch(chartData)}/>}
      </div>
      <div className='main-forecast' /* hidden={null == forecastWeather} */>
        {forecastWeather && <Forecast data={dispatch(forecastWeather)}/>}
      </div>
    </div>
  );
}

export default App;
