import './App.css';
import Search from './components/search/search';
import CurrentWeather from './components/current-weather/current-weather';
import { WEATHER_API_KEY, WEATHER_API_URL } from './api';
import { useState } from 'react';


function App() {
  const [currentWeather, setCurrentWeather] = useState(null);

  const handleOnSearchChange = (searchData) =>{
    const [lat] = searchData.label.split(' , ');
    const currentWeatherFetch = fetch (`${WEATHER_API_URL}/weather?q=${lat}&appid=${WEATHER_API_KEY}`)
    Promise.all([currentWeatherFetch])
      .then(async (response) => {
        const weather = await response[0].json();
        return setCurrentWeather({city: searchData.label, ...weather})
      })
      .catch((err) => console.log(err));
  }

  console.log(currentWeather)
  return (
    <div className="container">
      <Search onSearchChange={handleOnSearchChange}/>
      <CurrentWeather />
    </div>
  );
}

export default App;
