// import { Accordion, AccordionItem, AccordionItemButton, AccordionItemHeading, AccordionItemPanel } from "react-accessible-accordion";
import './forecast.css'
const WEEK_DAY = ['Mon', 'Tues', 'Wed', 'Thurs', 'Fri', 'Sat', 'Sun']
const MONTH = ['Jan', 'Feb', 'Mar', 'Apr', 'May', 'June', 'July', 'Aug','Sept','Oct','Nov','Dec']


const Forecast = ({ data }) => {
    const dayInAWeek = new Date().getDay();
    const forecastDays = WEEK_DAY.slice(dayInAWeek, WEEK_DAY.length).concat(WEEK_DAY.slice(0, dayInAWeek))


    return (
        <> 
            {data.list.splice(0, 10,2).map((item, idx) => (
                <div className="daily-item" key={idx}>
                    <img alt="weather" className="icon-small" src={`icons/${item.weather[0].icon}.png`} />
                    <div className="day">{forecastDays[idx]}, {MONTH[item.dt_txt.split('-')[1]-1]} {item.dt_txt.split('-')[2]} </div>
                    <div className="description">{item.weather[0].description}</div>
                    <div className="min-max">{Math.round(item.main.temp_min)}°C / {Math.round(item.main.temp_max)}°C</div>
                </div>  
                ))}
            <div className='month-item'>Month forecast</div>
        </>)
}

export default Forecast;
