import './App.css';
import Search from './components/search/search';
import Forecast from './components/forcast/forecast';
import CurrentWeather from './components/current-weather/current-weather';
import Visibility from './components/charts/charts';
import { WEATHER_API_KEY, WEATHER_API_URL } from './api';
import { useState } from 'react';


function App() {
  const [currentWeather, setCurrentWeather] = useState(null);
  const [forecastWeather, setForecastWeather] = useState(null);

  const handleOnSearchChange = (searchData) => {
    const [lat, lon] = searchData.value.split(" ");
    const city = searchData.label

    const currentWeatherFetch = fetch(`${WEATHER_API_URL}/weather?lat=${lat}&lon=${lon}&appid=${WEATHER_API_KEY}&units=metric`)
    const forecastFetch = fetch(`${WEATHER_API_URL}/forecast?lat=${lat}&lon=${lon}&appid=${WEATHER_API_KEY}&units=metric`)

    Promise.all([currentWeatherFetch, forecastFetch])
      .then(async(response) => {
        const weather = await response[0].json();
        const forecast = await response[1].json();

        setCurrentWeather({city: city, ...weather} )
        setForecastWeather( forecast )

      })
      .catch((err) => console.log(err));
  }

  console.log('currentWeather', currentWeather)
  console.log('forecastWeather', forecastWeather)

  return (
    <div className="body">
      <link href="https://fonts.googleapis.com/css2?family=Montserrat:wght@300&display=swap" rel="stylesheet"></link>    
      <div className="search">
        <Search onSearchChange={handleOnSearchChange} />
      </div>
      <div className='main-weather'>
        {currentWeather && <CurrentWeather data={currentWeather} />}
      </div>
      {/* <div className='vis-chart'>
        {forecastWeather && <Visibility data={forecastWeather}/>}
      </div> */}
      <div className='main-forecast'>
        {forecastWeather && <Forecast data={forecastWeather}/>}
      </div>
    </div>
  );
}

export default App;
